mapper.insert:
INSERT INTO movie
(	  movie_name
	, movie_director
	, movie_poster
	, movie_trailler
	, movie_open_date
	, movie_end_date
	, movie_status
	, movie_grade
	, movie_summary
	, movie_runtime
	, movie_stillCut
	, movie_stillCut2
	, movie_stillCut3
	, movie_genre
)
VALUES
(	  #{movie_name}
	, #{movie_director}
	, #{movie_poster}
	, #{movie_trailler}
	, #{movie_open_date}
	, #{movie_end_date}
	, #{movie_status}
	, #{movie_grade}
	, #{movie_summary}
	, #{movie_runtime}
	, #{movie_stillCut}
	, #{movie_stillCut2}
	, #{movie_stillCut3}
	, #{movie_genre}
)

mapper.update:
UPDATE movie
SET movie_name = #{movie_name}
  , movie_director = #{movie_director}
  , movie_poster = #{movie_poster}
  , movie_trailler = #{movie_trailler}
  , movie_open_date = #{movie_open_date}
  , movie_end_date = #{movie_end_date}
  , movie_status = #{movie_status}
  , movie_grade = #{movie_grade}
  , movie_summary = #{movie_summary}
  , movie_runtime = #{movie_runtime}
  , movie_stillCut = #{movie_stillCut}
  , movie_stillCut2 = #{movie_stillCut2}
  , movie_stillCut3 = #{movie_stillCut3}
  , movie_genre = #{movie_genre}
WHERE movie_num = #{movie_num}

mapper.insert:
INSERT INTO event(
	event_subject
	, event_type_num
	, event_reg_date
	, event_start_date
	, event_end_date
	, event_thumbnail
	, event_image
	, coupon_num
)
VALUES(
	#{event_subject}
	, #{event_type_num}
	, now()
	, #{event_start_date}
	, #{event_end_date}
	, #{event_thumbnail}
	, #{event_image}
	, #{coupon_num}
)

mapper.update:
UPDATE event
SET 
	event_subject = #{event_subject}
	, event_type_num = #{event_type_num}
	, event_start_date = #{event_start_date}
	, event_end_date = #{event_end_date}
	, coupon_num = #{coupon_num}
WHERE
	event_num = #{event_num}

UPDATE item_info
SET 
	item_info_category = #{item_info_category}
	, item_info_price = #{item_info_price}
	, item_info_image = #{item_info_image}
WHERE
	item_info_name = #{item_info_name}
