mapper.select:
SELECT e.*, et.event_type_name FROM event e JOIN event_type et ON e.event_type_num = et.event_type_num
WHERE e.event_subject LIKE CONCAT('%', #{searchKeyword}, '%') OR et.event_type_name LIKE CONCAT('%', #{searchKeyword}, '%')
LIMIT #{startRow} , #{listLimit}

mapper.insert:

	SELECT coupon_type_num
	FROM event
	WHERE event_num = #{event.event_num}

	INSERT INTO 
		coupon
	VALUES (
		null
		, #{coupon_type_num}
		, #{id}
		, 1
	)
