mapper.select:
SELECT faq_num, faq_subject, faq_content, faq_category, faq_read_count FROM faq ORDER BY faq_num DESC
WHERE faq_category = #{faqCategory}
LIMIT #{startRow}, #{listLimit}

mapper.update:
UPDATE faq
SET faq_subject = #{faq_subject},
	faq_content = #{faq_content},
	faq_category = #{faq_category}
WHERE faq_num = #{faq_num}

mapper.select:
  		SELECT COUNT(faq_num)
  		FROM faq
  		
	  		WHERE faq_category = #{faqCategory}

SELECT COUNT(faq_num)
FROM faq
WHERE faq_category = #{faq_category}
