mapper.select:
SELECT o.oto_num, o.oto_subject, o.oto_content, o.oto_date, o.oto_category, o.oto_reply_status, o.theater_num, o.member_id, t.theater_name FROM oto o JOIN theater t on o.theater_num = t.theater_num
WHERE oto_category = #{faqCategory}
WHERE theater_name = #{theaterName}
ORDER BY oto_num DESC LIMIT #{startRow}, #{listLimit}

SELECT theater_num 
FROM theater
WHERE theater_name = #{theater_name}

mapper.update:
UPDATE oto
SET oto_content = #{oto_content}
WHERE oto_num = #{oto_num}

UPDATE oto
SET oto_reply_status = '답변'
WHERE oto_num = #{oto_num}

mapper.select:
		SELECT COUNT(oto_num)
		FROM oto o
		JOIN theater t
   		on o.theater_num = t.theater_num
		
   			WHERE oto_category = #{faqCategory}
   		
   		
   			WHERE theater_name = #{theaterName}
